mapper.select:
select
unitName,name,cardId,staffId,birthday,native,job,staff.unitId,title,staff.telephone,joinTime,sex
from staff,unit where staffId=#{staffId} and staff.unitId=unit.unitId;

select
unitName,name,cardId,staffId,birthday,native,job,staff.unitId,title,staff.telephone,joinTime,sex
from staff,unit where staff.unitId=#{unitId} and staff.unitId=unit.unitId;

mapper.update:
update staff set
name=#{name}
cardId=#{cardId}
birthday=#{birthday}
native=#{native}
job=#{job}
unitId=#{unitId}
title=#{title}
telephone=#{telephone}
joinTime=#{joinTime}
sex=#{sex} where staffId=#{staffId}
